import './footer.css';
import 'react-slideshow-image/dist/styles.css'
import logo from '../img/Logo.png';
import { Col, Row, Container, Card, Button, Carousel} from "react-bootstrap";
import { LoremIpsum } from 'react-lorem-ipsum';
import { faLongArrowAltDown } from '@fortawesome/free-solid-svg-icons';

import { Form, Field, FormElement } from '@progress/kendo-react-form';
import { Error } from '@progress/kendo-react-labels';
import { Input } from '@progress/kendo-react-inputs';

const emailRegex = new RegExp(/\S+@\S+\.\S+/);
const emailValidator = (value) => (emailRegex.test(value) ? "" : "Please enter a valid email.");
const EmailInput = (fieldRenderProps) => {
  const { validationMessage, visited, ...others } = fieldRenderProps;
  return (
    <div>
      <Input {...others} />
      {
        visited && validationMessage &&
          (<Error>{validationMessage}</Error>)
      }
    </div>
  );
};

function Footer() {
    const handleSubmit = (dataItem) => alert(JSON.stringify(dataItem, null, 2));
  return (
    <Container className="container" fluid>
        <Row className="logoFooter">
            <Col></Col>
            <Col><img src={logo} alt="logoFooter"></img></Col>
            <Col></Col>
        </Row>
        <Row>
        <Col lg={2}></Col>
            <Col lg={8} className="footerLabel"><h3>Aboniere unsere Newsletter<br/>fur wochentliche Gesunheitstipps</h3></Col>
            <Col lg={2}></Col>
        </Row>
        <Row></Row>
        <Row></Row>
        <Form
     onSubmit={handleSubmit}
     render={(formRenderProps) => (
         <FormElement style={{maxWidth: 650}}>
             <fieldset className={'k-form-fieldset'}>
                 <legend className={'k-form-legend'}>Please fill in the fields:</legend>
                 <div className="mb-3">
                     <Field name={'firstName'} component={Input} label={'First name'} />
                 </div>

                 <div className="mb-3">
                     <Field name={'lastName'} component={Input} label={'Last name'} />
                 </div>

                 <div className="mb-3">
                     <Field name={"email"} type={"email"} component={EmailInput} label={"Email"} validator={emailValidator} />
                 </div>
             </fieldset>
             <div className="k-form-buttons">
                 <button
                     type={'submit'}
                     className="k-button"
                     disabled={!formRenderProps.allowSubmit}
                 >
                     Submit
                 </button>
             </div>
         </FormElement>
     )}
 />     
  </Container>      
  );
}

export default Footer;
